import React, { useEffect, useState } from "react";
import axiosInstance from "../axios";
import { useParams } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { Button, Box } from "@material-ui/core";
import useLogin from "../stores/loginStore";


function SinglePost() {
  const { petId } = useParams();
  const [pet, setPet] = useState([]);
  const [buttonText, setButtonText] = useState("Request adoption");
  const [buttonDisable, setButtonDisable] = useState(false);
  let access_token = localStorage.getItem("access_token");
  const loginState = useLogin((state) => state.loggedIn);
  // console.log(access_token);
  let userId;
  if (access_token !== null) {
    userId = jwtDecode(access_token)?.user_id;
    // console.log(userId);
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    axiosInstance
      .post(`applications/`, {
        pet: petId,
        // user: userId,
        // password: formData.password,
      })
      .then((res) => {
        setButtonText("request submitted");
        setButtonDisable(true);
      })
      .catch((e) => console.error(e.message));
  };

  useEffect(() => {
    axiosInstance
      .get(`pets/${petId}`)
      .then((res) => {
        const singlePet = res.data;
        setPet(singlePet);
        // console.log(res.data);
      })
      .catch((e) => {
        console.error(e.message);
      });

    axiosInstance
      .get("applications")
      .then((res) => {
        const applications = res.data;
        const matchedApplication = applications.filter(
          (application) =>
            application.user === userId && application.pet === petId
        );
        if (matchedApplication.length !== 0) {
          setButtonText("request submitted");
          setButtonDisable(true);
        }
      })
      .catch((e) => console.error(e.message));
  }, [petId, userId, setPet]);

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      minHeight="80vh"
    >
      <Card className={classes.card}>
        <CardMedia
          className={classes.cardMedia}
          image={post.image}
          title="Image title"
        />
        <CardContent className={classes.cardContent}>
          <Typography
            gutterBottom
            variant="h6"
            component="h2"
            className={classes.postTitle}
          >
            {post.name}
          </Typography>
          <div className={classes.postText}>
            <Typography color="textSecondary">
              {post.description.substr(0, 30)}...
            </Typography>
          </div>
        </CardContent>
      </Card>
      <div>{pet.name}</div>
      <div>{pet.species}</div>
      <div>{pet.breed}</div>

      {loginState && (
        <Button
          disabled={buttonDisable}
          type="submit"
          variant="contained"
          color="primary"
          onClick={handleSubmit}
        >
          {buttonText}
        </Button>
      )}
    </Box>
  );
}

export default SinglePost;
