import React, { useEffect, useState } from "react";
import axiosInstance from "../axios";
import { useParams } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { Button, Box } from "@material-ui/core";
import useLogin from "../stores/loginStore";
import Typography from "@material-ui/core/Typography";

function SinglePost() {
  const { petId } = useParams();
  const [pet, setPet] = useState([]);
  const [buttonText, setButtonText] = useState("Request adoption");
  const [buttonDisable, setButtonDisable] = useState(false);
  let access_token = localStorage.getItem("access_token");
  const loginState = useLogin((state) => state.loggedIn);
  // console.log(access_token);
  let userId;
  if (access_token !== null) {
    userId = jwtDecode(access_token)?.user_id;
    // console.log(userId);
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    axiosInstance
      .post(`applications/`, {
        pet: petId,
        // user: userId,
        // password: formData.password,
      })
      .then((res) => {
        setButtonText("request submitted");
        setButtonDisable(true);
      })
      .catch((e) => console.error(e.message));
  };

  useEffect(() => {
    axiosInstance
      .get(`pets/${petId}`)
      .then((res) => {
        const singlePet = res.data;
        setPet(singlePet);
        // console.log(res.data);
      })
      .catch((e) => {
        console.error(e.message);
      });

    axiosInstance
      .get("applications")
      .then((res) => {
        const applications = res.data;
        const matchedApplication = applications.filter(
          (application) =>
            application.user === userId && application.pet === petId
        );
        if (matchedApplication.length !== 0) {
          setButtonText("request submitted");
          setButtonDisable(true);
        }
      })
      .catch((e) => console.error(e.message));
  }, [petId, userId, setPet]);

  return (
    <Box
      display="flex"
      flexDirection="row"
      justifyContent="center"
      alignItems="center"
      minHeight="80vh"
    >
      <Box
        component="img"
        sx={{
          height: 333,
          width: 350,
          maxHeight: { xs: 233, md: 167 },
          maxWidth: { xs: 350, md: 250 },
        }}
        alt="pet for adoption"
        src={pet.image}
      />

      <Box p={3}>
        {/* <Typography gutterBottom variant="h3" component="h2">
          {pet.name}
        </Typography>
        <Typography gutterBottom variant="h6" component="h6">
          species: {pet.species}
        </Typography>
        <Typography gutterBottom variant="h6" component="h6">
          breed: {pet.breed}
        </Typography> */}
        <h5>name </h5>
        <p>{pet.description}</p>

        {loginState && (
          <Button
            disabled={buttonDisable}
            type="submit"
            variant="contained"
            color="primary"
            onClick={handleSubmit}
          >
            {buttonText}
          </Button>
        )}
      </Box>
    </Box>
  );
}

export default SinglePost;
